import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Corrected the import statement

function App2() {
  const [data, setUserData] = useState([]);
  const [userid, setUserId] = useState("");
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const updateUserId = (event) => {
    setUserId(event.target.value);
  };

  const updateEmail = (event) => {
    setEmail(event.target.value);
  };

  const updatePassword = (event) => {
    setPassword(event.target.value);
  };

  const insertUsers = (event) => {
    event.preventDefault(); // Prevent form submission and page reload

    axios.post('http://localhost:9901/insert', { userid:userid, emailid:email, password:password})
      .then((response) => {
        // Handle the response as needed
        console.log(response.data);
        // Clear input fields
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  function fetchdata() {
    axios.get('http://localhost:9901/getAll')
      .then((response) => {
        setUserData(response.data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }

  function clear(){
    setUserData('');
    setUserId('');
    setEmail('');
  }

  return (
    <div className="App">
      <form onSubmit={insertUsers} onReset={clear}>
        <b>User ID</b>
        <input type="text" value={userid} onChange={updateUserId} /><br />
        <b>Email ID</b>
        <input type="text" value={email} onChange={updateEmail} /><br />
        <b>Password</b>
        <input type="password" value={password} onChange={updatePassword} /><br />
        <button type="submit">Add</button>&nbsp;&nbsp;
        <button type="reset">Reset</button>
      </form>
      <button onClick={fetchdata}>Show</button>
      <center>
        <ul>
          {data.map((item) => (
            <li key={item.userid}>ID: {item.userid},Password: {item.password}, email: {item.emailid}</li>
          ))}
        </ul>
      </center>
    </div>
  );
}

export default App2;






